import Navbar from "./Navbar";
import Footer from "./Footer";
import { Outlet } from "react-router-dom";
import React, { useEffect, useState } from "react";


function Layout() {
  const [scroll, setScroll] = useState(window.scrollY);

  function moveToTop() {
    window.scroll({
      top: 0,
      behavior: "smooth",
    });
  }

  useEffect(() => {
    function handleScroll() {
      setScroll(window.scrollY);
    }
    window.addEventListener("scroll", handleScroll);

    // Clean-up function
    return function () {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [scroll]);
  return (
    <>
      <div>
        {scroll > 500 && (

          <img src="/toy-images/finger-bg.png" onClick={moveToTop} style={{
            position: "fixed",
            right: "50px",
            bottom: "50px",
            padding: "16px",
            height: "100px",
            width: "100px",
            objectFit: "cover",

          }} />
        )}
        <h2 style={{
          position: "fixed",
          right: "35px",
          bottom: "30px",
          padding: "16px",

          objectFit: "cover",
        }}> Move To Top</h2>
      </div >
      <Navbar />
      <Outlet />
      <Footer />
    </>
  );
}

export default Layout;
